    private void jDateChooser3PropertyChange(java.beans.PropertyChangeEvent evt) {//GEN-FIRST:event_jDateChooser3PropertyChange
        jDateChooser4.setSelectableDateRange(jDateChooser3.getDate(), new java.util.Date());
        jDateChooser4.setDate(jDateChooser3.getDate());
    }//GEN-LAST:event_jDateChooser3PropertyChange

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // TODO add your handling code here:

        if(jDateChooser3.getDate()!=null && jDateChooser4.getDate()!=null){

            java.util.Date fromDate = (java.util.Date) jDateChooser3.getDate();
            java.util.Date toDate = (java.util.Date) jDateChooser4.getDate();

            java.sql.Date fDate = new java.sql.Date(fromDate.getTime());
            java.sql.Date tDate = new java.sql.Date(toDate.getTime());

        
            try{

                javax.swing.table.DefaultTableModel dtm = (javax.swing.table.DefaultTableModel)jTable2.getModel();

                int count = dtm.getRowCount();

                while(count-- > 0){
                    dtm.removeRow(0);
                }
                
                float TotalDays = 0f;
                float TotalPresent = 0f;
                

                ResultSet rs = db.dbConnect.st.executeQuery("Select * from faculty_attendence where date>='"+fDate+"' and date<='"+tDate+"' and fid='"+Login.AdminOrFacultyLogin.id+"' order by date asc");
                boolean isDataPresent = false;
                while(rs.next()){
                    
                    isDataPresent = true;
                    Vector v = new Vector();

                    v.add(rs.getDate("date"));
                    v.add(rs.getString("attendence"));
                    
                    if(rs.getString("attendence") != null && rs.getString("attendence").equals("Present")){
                        TotalPresent++;
                        TotalDays++;
                    }
                    
                    if(rs.getString("attendence") != null && !rs.getString("attendence").equals("Present")){
                        TotalDays++;
                    }
                    
                    dtm.addRow(v);

                }
                
                if(!isDataPresent){
                    JOptionPane.showMessageDialog(null, "No Attendence Marked Between Entered Dates");
                }
                
                if(isDataPresent){
                    String str = String.format("%.2f", (TotalPresent/TotalDays)*100);
                jLabel10.setText(str+"%");
                }
                
            }catch(Exception e){
                JOptionPane.showMessageDialog(null, e);
            }
        }else{
            JOptionPane.showMessageDialog(null,"Please Enter All Fields");
        }
    }//GEN-LAST:event_jButton2ActionPerformed

