    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed

        if(jDateChooser1.getDate()!=null){

            java.util.Date fromDate = (java.util.Date) jDateChooser1.getDate();
           

            java.sql.Date fDate = new java.sql.Date(fromDate.getTime());

            try{

                javax.swing.table.DefaultTableModel dtm = (javax.swing.table.DefaultTableModel)jTable1.getModel();

                int count = dtm.getRowCount();

                while(count-- > 0){
                    dtm.removeRow(0);
                }

                ResultSet rs = db.dbConnect.st.executeQuery("Select * from faculty_attendence where date='"+fDate+"' and fid='"+Login.AdminOrFacultyLogin.id+"' ");

                boolean isDataPresent = false;

                while(rs.next()){
                    isDataPresent = true;
                    Vector v = new Vector();

                    v.add(rs.getDate("date"));
                    v.add(rs.getString("attendence"));

                    dtm.addRow(v);
                    
                }
                if(!isDataPresent){
                    JOptionPane.showMessageDialog(null, "No Attendence Marked On Entered Dates");
                }

            }catch(Exception e){
                JOptionPane.showMessageDialog(null, e);
            }
        }else{
            JOptionPane.showMessageDialog(null, "Please Enter All Fields");
        }

    }//GEN-LAST:event_jButton1ActionPerformed

